const bcChange = ()=>{
  let newbg = "#34495e";    
  setBg(newbg)  ;
  setname("Ouch!!! ðŸ¦Š"); 
} 

const bgBack = ()=>{
  setBg(purple);
  setname('Ayya !! ðŸ˜ ');
}

 return (
   <>
     <div style={{backgroundColor: bg}}>
       <button onClick={bcChange} onDoubleClick={bgBack}> {name}</button>
     </div>
   </>
   )
}
 export default App;
